    def inserting(self):
        b1=self.lineEdit_21.text()
        name=self.lineEdit_22.text()
        b2=self.lineEdit_23.text()
        if(len(b1)==0 or len(name)==0 or len(b2)==0):
            self.label_61.setText("Fill all the Details ")
            
        else:
         id=int(b1)
         price=int(b2)
         client=pymongo.MongoClient('mongodb://127.0.0.1:27017/')
         mydb=client['items']
         info=mydb.items1
         ins=[{"id" :id , "name" : name, "price" : price}]
         info.insert_many(ins)
         self.label_61.setText("new item added sucessfully")
        self.lineEdit_21.setText("")
        self.lineEdit_22.setText("")
        self.lineEdit_23.setText("")
    def deletion(self):
        bg=self.lineEdit_38.text()
        if(len(bg)==0):
            self.label_39.setText("Enter the Item ID")
        else:
            bg1=int(bg)
            client=pymongo.MongoClient('mongodb://127.0.0.1:27017/')
            mydb=client['items']
            info=mydb['items1']
            dels={"id":bg1}
            info.delete_one(dels)
            self.label_39.setText(" Item has deleted ")
        self.lineEdit_38.setText("")
    def updater(self):
        cg=self.lineEdit_36.text()
        cgg=self.lineEdit_37.text()
        if(len(cg)==0 or len(cgg)==0):
            self.label_12.setText("Fill Both The Inputs")
        else:
            cg1=int(cg)
            cgg1=int(cgg)
            client=pymongo.MongoClient('mongodb://127.0.0.1:27017/')
            mydb=client['items']
            info=mydb['items1']
            info.update_one({"id":cg1},{"$set":{"price":cgg1}})
            self.label_12.setText(" Item's price is Updated ")
        self.lineEdit_36.setText("")
        self.lineEdit_37.setText("")
    def monadder(self):
        a=int(self.lineEdit.text())*5
        b=int(self.lineEdit_2.text())*10
        c=int(self.lineEdit_3.text())*20
        d=int(self.lineEdit_4.text())*50
        e=int(self.lineEdit_5.text())*100
        self.amount=a+b+c+d+e
        ss=str(self.amount)
        self.teee=self.amount
        self.label_34.setText(ss+" Rupees has been added")
        self.lineEdit.setText("0")
        self.lineEdit_2.setText("0")
        self.lineEdit_3.setText("0")
        self.lineEdit_4.setText("0")
        self.lineEdit_5.setText("0")
    def rerr(self):
        self.amount=0
        self.textEdit.setText("")
        self.label_34.setText("Your money has returned")
    def searcher(self):
        client=pymongo.MongoClient('mongodb://127.0.0.1:27017/')
        mydb=client['items']
        info=mydb['items1']
        res=info.find({"price":{"$lte":self.amount}})
        for i in res:
            idd=str(i["id"])
            ppr=str(i["price"])
            bbrb=str(self.amount)
            self.textEdit.append("      "+idd+"                               "+i["name"]+"                                 "+ppr)
        self.label_34.setText("Select the Items in the list still "+bbrb+" rupees left")
    def additemss(self):
        itid=self.lineEdit_13.text()
        if(len(itid)==0):
            self.label_34.setText("Enter the ItemID to Add ")
        else:
            itid1=int(itid)
            client=pymongo.MongoClient('mongodb://127.0.0.1:27017/')
            mydb=client['items']
            info=mydb['items1']
            res=info.find({"id":itid1})
            pr=0
            for i in res:
                pr=i["price"]
                nr=i["name"]
            self.cost=self.cost+pr
            self.amount=self.amount-pr
            self.textEdit.setText("")
            self.label_34.setText(nr+" is Added")
        self.lineEdit_13.setText("")
    def bille(self):
        amdd=str(self.teee)
        bilr=str(self.cost)
        chng=str(self.amount)
        self.label_34.setText("ammount added="+amdd+"\n"+"Bill is ="+bilr+"\n"+"Balance is = "+chng)
    def exee(self):
        sys.exit()
        
        
            
                          
            
            
        
        
